INSERT INTO doctors (id, name, gender, department, title, phone, email, specialization, introduction, consultation_fee, available, created_at, updated_at) VALUES
(10, '李国华', 'MALE', '内科', '主任医师', '010-85123456', 'liguohua@hospital.com', '心血管疾病、高血压、冠心病、心律失常的诊治', '从事内科临床工作25年，擅长心血管疾病的诊断和治疗。曾在北京安贞医院进修心血管专业，在冠心病、高血压、心律失常等疾病的治疗方面积累了丰富的临床经验。发表学术论文20余篇。', 80.00, true, NOW(), NOW()),
(11, '王淑敏', 'FEMALE', '妇产科', '副主任医师', '010-85123457', 'wangshumin@hospital.com', '妇科肿瘤、不孕不育、产前诊断、妇科内分泌', '妇产科专业，具有18年临床经验。擅长妇科常见病、多发病的诊治，在宫颈疾病、子宫肌瘤、卵巢囊肿等妇科疾病的微创治疗方面有独到见解。曾在协和医院妇产科进修学习。', 70.00, true, NOW(), NOW()),
(12, '张建军', 'MALE', '骨科', '主任医师', '010-85123458', 'zhangjianjun@hospital.com', '脊柱外科、关节置换、创伤骨科、骨肿瘤', '骨科专家，从事骨科临床工作22年。擅长脊柱疾病、关节疾病的外科治疗，完成各类骨科手术2000余例。在脊柱侧弯矫形、人工关节置换等高难度手术方面有丰富经验。', 90.00, true, NOW(), NOW()),
(13, '赵小燕', 'FEMALE', '儿科', '主治医师', '010-85123459', 'zhaoxiaoyan@hospital.com', '儿童呼吸系统疾病、消化系统疾病、儿童保健', '儿科临床工作12年，对儿童常见病、多发病有丰富的诊治经验。擅长小儿肺炎、哮喘、腹泻病等疾病的诊治，在儿童生长发育评估和营养指导方面经验丰富，深受患儿家长信任。', 50.00, true, NOW(), NOW()),
(14, '孙志远', 'MALE', '神经内科', '主任医师', '010-85123460', 'sunzhiyuan@hospital.com', '脑血管病、帕金森病、癫痫、头痛、眩晕的诊治', '神经内科专家，从事神经系统疾病诊治28年。在脑血管病的预防和治疗、帕金森病的综合治疗、癫痫的精准诊疗方面有深入研究。主持完成省级科研项目3项，发表学术论文35篇。', 100.00, true, NOW(), NOW()),
(15, '刘春梅', 'FEMALE', '眼科', '副主任医师', '010-85123461', 'liuchunmei@hospital.com', '白内障、青光眼、眼底病、屈光不正的诊治', '眼科临床工作16年，擅长各种眼科常见病、多发病的诊治。在白内障超声乳化术、青光眼手术、眼底激光治疗等方面技术精湛。完成各类眼科手术3000余例，手术成功率高。', 60.00, true, NOW(), NOW()),
(16, '马德华', 'MALE', '泌尿外科', '主任医师', '010-85123462', 'madehua@hospital.com', '泌尿系结石、前列腺疾病、泌尿系肿瘤', '泌尿外科专家，从事泌尿外科临床工作20年。擅长泌尿系结石的微创治疗、前列腺增生的手术治疗、泌尿系肿瘤的综合治疗。在腹腔镜手术、输尿管软镜碎石等微创技术方面有丰富经验。', 85.00, true, NOW(), NOW());
INSERT INTO registrations (id, patient_id, doctor_id, appointment_time, registration_fee, status, symptoms, diagnosis, treatment, prescription, notes, created_at, updated_at) VALUES
(10, 10, 10, '2024-01-15 09:00:00', 80.00, 'COMPLETED', '头痛、头晕3天，伴有恶心症状，血压升高', '原发性高血压（2级），高血压性头痛', '1. 降压治疗：厄贝沙坦氢氯噻嗪片 150mg/12.5mg qd\n2. 对症治疗：布洛芬缓释胶囊 0.3g bid\n3. 生活方式指导：低盐低脂饮食，戒烟限酒，适度运动\n4. 血压监测：建议家庭自测血压', '1. 厄贝沙坦氢氯噻嗪片 150mg/12.5mg × 28片\n2. 布洛芬缓释胶囊 0.3g × 12粒\n3. 硝苯地平缓释片 30mg × 28片', '患者血压165/105mmHg，心率78次/分，建议定期监测血压，控制目标＜140/90mmHg，2周后复诊调整药物剂量', NOW(), NOW()),
(11, 11, 11, '2024-01-16 14:30:00', 70.00, 'COMPLETED', '月经不调3个月，量少色暗，伴有腰酸乏力', '月经失调，卵巢功能减退可能', '1. 激素调理：戊酸雌二醇片 1mg qd × 21天\n2. 中药调理：逍遥丸 6g tid\n3. 生活调理：规律作息，避免熬夜，适当运动\n4. 营养补充：叶酸片、维生素E', '1. 戊酸雌二醇片 1mg × 21片\n2. 逍遥丸 6g × 21袋\n3. 叶酸片 5mg × 30片\n4. 维生素E软胶囊 100mg × 30粒', '建议做妇科超声检查和性激素六项检查，了解子宫内膜厚度和卵巢功能，3个月后复查', NOW(), NOW()),
(12, 12, 12, '2024-01-17 10:15:00', 90.00, 'IN_PROGRESS', '腰痛1月余，活动受限，弯腰困难', '腰椎间盘突出症（L4-5），腰肌劳损', '1. 急性期卧床休息2-3天\n2. 消炎镇痛：双氯芬酸钠缓释片 75mg qd\n3. 营养神经：甲钴胺片 0.5mg tid\n4. 物理治疗：腰椎牵引，理疗\n5. 功能锻炼：腰背肌锻炼', '1. 双氯芬酸钠缓释片 75mg × 14片\n2. 甲钴胺片 0.5mg × 42片\n3. 复方氯唑沙宗片 × 21片', '建议做腰椎MRI检查明确诊断，避免长时间弯腰和久坐，睡硬板床，如症状不缓解考虑手术治疗', NOW(), NOW()),
(13, 13, 13, '2024-01-18 08:45:00', 50.00, 'COMPLETED', '咳嗽、咳痰1周，夜间加重，伴低热', '上呼吸道感染，支气管炎', '1. 抗感染治疗：阿奇霉素干混悬剂 0.125g bid × 3天\n2. 止咳化痰：氨溴索糖浆 10ml tid\n3. 对症治疗：复方甘草片 3片 tid\n4. 物理治疗：多饮水，蒸气吸入', '1. 阿奇霉素干混悬剂 0.125g × 6袋\n2. 氨溴索糖浆 100ml × 1瓶\n3. 复方甘草片 × 60片\n4. 维生素C泡腾片 × 20片', '注意休息，多饮水，避免辛辣刺激性食物，如症状加重或持续不缓解及时复诊', NOW(), NOW()),
(14, 14, 14, '2024-01-19 11:20:00', 100.00, 'COMPLETED', '突发头痛2天，伴有恶心呕吐，视物模糊', '偏头痛，血管神经性头痛', '1. 急性期治疗：舒马曲坦片 50mg 必要时服用\n2. 预防性治疗：托吡酯片 25mg bid\n3. 对症治疗：甲氧氯普胺片 10mg tid\n4. 生活调节：规律作息，避免诱发因素', '1. 舒马曲坦片 50mg × 6片\n2. 托吡酯片 25mg × 56片\n3. 甲氧氯普胺片 10mg × 21片', '建议做头颅CT或MRI排除器质性病变，避免熬夜、饮酒等诱发因素，如频繁发作需调整预防用药', NOW(), NOW()),
(15, 15, 15, '2024-01-20 16:00:00', 60.00, 'PENDING', '视力下降6个月，看东西模糊，怕光', '老年性白内障（双眼），屈光不正', '建议手术治疗：白内障超声乳化联合人工晶体植入术', '术前检查：眼科A超、角膜内皮计数、眼底检查', '患者双眼白内障已影响日常生活，建议择期手术治疗，先处理严重眼，术后视力可明显改善', NOW(), NOW()),
(16, 16, 16, '2024-01-21 13:15:00', 85.00, 'COMPLETED', '尿频、尿急、尿痛1周，夜尿增多', '急性膀胱炎，前列腺增生症', '1. 抗感染治疗：左氧氟沙星片 0.5g bid × 7天\n2. α受体阻滞剂：坦索罗辛缓释胶囊 0.2mg qd\n3. 对症治疗：三金片 4片 tid\n4. 大量饮水，勤排尿', '1. 左氧氟沙星片 0.5g × 14片\n2. 坦索罗辛缓释胶囊 0.2mg × 28粒\n3. 三金片 × 84片', '建议做尿常规和前列腺超声检查，多饮水，避免憋尿，如症状不缓解需进一步检查', NOW(), NOW());
